@Controller('cards')
export class CardsController {
  constructor(private readonly service: CardsService) {}

  @Get('get')
  async getSet(@Query('id') setId: string): Promise<
    | {
        id: string;
        userId: string;
        name: string;
        cards: { q: string; a: string;}[];
        parentId: string|null;
      }
    | string
  > {
    return await this.service.getCardSetById(setId);
  }

  @Post('create')
  async createSet(@Body() set: CreateCardsSetDto): Promise<{
    id: string;
    name: string;
    cards: { q: string; a: string; }[];
    parentId: string|null;
  }> {
    let createdSet: CardsSet = { name: set.name, userId: set.userId, parentId:set.parentId };
    createdSet = await this.service.createCardSet(createdSet);
    if (!createdSet) throw new Error('Error creating set');
    const cards: Card[] = await this.service.createCards(
      set.cards.map((card) => {
        const newCard: Card = {
          q: card.q,
          a: card.a,
          setId: createdSet['_id'].toString(),
        };
        return newCard;
      }),
    );

    return {
      id: createdSet['_id'].toString(),
      name: createdSet.name,
      cards: cards.map((card) => {
        return {
          q: card.q,
          a: card.a,
        };
      }),
      parentId: createdSet.parentId,
    };
  }

  @Delete('remove')
  async removeSet(@Query('id') setId: string): Promise<boolean> {
    try {
      await this.service.deleteCardSet(setId);
      return true;
    } catch (e) {
      return false;
    }
  }

  @Put('update')
  async updateSet(@Body() set: UpdateCardsSetDto): Promise<boolean> {
    try {
      await this.service.updateCardSet(set.id, { name: set.name, userId: set.userId, parentId:set.parentId});
      const cards: Card[] = await this.service.getCardsBySetId(set.id);
      await Promise.all( cards.map((card) => this.service.deleteCard(card['_id'].toString())) );
      await this.service.createCards(
        set.cards.map((card) => {
          const newCard: Card = {
            q: card.q,
            a: card.a,
            setId: set.id,
          };
          return newCard;
        }
      ));
      return true;
    }
    catch (e) {
      return false;
    }
  }
}
