import genMetadata from "./generator/estimate.process";
import config from "./generator/.gen.config";
import NameGenerator from "./generator/name.gen";

function convertObservable(observable: Observable<Object>): Observable<{data:string}>{
  return new Observable((subscriber) => {
    observable.subscribe({
      next: (value) => {
        subscriber.next({data:JSON.stringify(value)});
      },
      error: (error) => {
        subscriber.error(error);
      },
      complete: () => {
        subscriber.complete();
      }
    });
  });
}

@Injectable()
export class GenService {
  cardGenerator = new CardsGenerator();
  layoutFixGenerator = new LayoutGenerator();
  nameGenerator = new NameGenerator();
  generateCards(content: string, lang: string): Observable<{ data: string; }> {
    if(!content){
      throw new EmptyError();
    }

    const cardGenerateObservable = this.cardGenerator.generateCards(content, 'default', lang);
    return convertObservable(cardGenerateObservable);
  }

  getMetadata(content: string): { needLayoutFix?:boolean, needLayoutFixReason?:string, error?: string, fixCost?: number, genCost?: number, language?: { code: string; name: string; nativeName: string; country: string; flag: string; }} {
    if(!content){
      throw new EmptyError();
    }

    return genMetadata(content);
  }

  async generateVariations(card: Card): Promise<Card> {
    return new Promise((resolve, reject) => { resolve(card); });
  }

  async generateSetName(content: string, lang:string) {
    if(!content){
      throw new EmptyError();
    }

    return await this.nameGenerator.generateSetName(content, lang);
  }

  generateLayoutFix(content: string): Observable<{ data: string; }> {
    if(!content){
      throw new EmptyError();
    }

    const layoutFixGenerateObservable = this.layoutFixGenerator.generateLayout(content, 'default');
    return convertObservable(layoutFixGenerateObservable);
  }
}
