 "import { Injectable } from '@nestjs/common';
import { InjectModel } from '@nestjs/mongoose';
import { Folder, FolderDocument } from './schema/folder.schema';
import { Model } from 'mongoose';
import { CardsSet, CardsSetDocument } from '../cards/schema/cards-set.schema';
import { CardsService } from '../cards/cards.service';

@Injectable()
export class FolderService {
  constructor(
    @InjectModel(Folder.name) private FolderModel: Model<FolderDocument>,
    @InjectModel(CardsSet.name) private cardModel: Model<CardsSetDocument>,
    private readonly cardSetService: CardsService,
  ) {}

  async getUserFolderIndex(userId: string): Promise<{
    id: string;
    name: string;
    color: string;
    parentId: string;
  }[]> {
    const folders = await this.FolderModel.find({ userId: userId}).exec();
    const indexedFolders = [];
    for (const folder of folders) {
      const id = folder['_id'].toString();
      const populatedFolder = {
        id: id,
        name: folder.name,
        color: folder.color,
        parentId: folder.parentId,
      };
      indexedFolders.push(populatedFolder);
    }
    return indexedFolders;
  }

  async getUserFolder(
    userId: string,
    folderId?: string,
  ): Promise<{
    folder?: Folder;
    content: { folders: Folder[]; cardSets: CardsSet[] };
  }> {
    if (folderId === 'root' || folderId === 'null') folderId = null;
    let folder: Folder | null = null;
    if (folderId) {
      folder = await this.FolderModel.findById(folderId).exec();
      if (!folder) return null;
    }
    const content = await this.getUserFolderContent(userId, folderId);
    return { folder: folder, content: content };
  }

  async getUserFolderContent(
    userId: string,
    folderId: string | null = null,
  ): Promise<{ folders: {  id: string;
      userId: string;
      n: number;
      name: string;
      color: string;
      parentId: string; }[]; cardSets: CardsSet[] }> {
    if (folderId === 'root') folderId = null;
    // get all folders with parent id = null
    const childFolders = await this.FolderModel.find({
      userId: userId,
      parentId: folderId,
    }).exec();

    const indexedChildFolders = [];
    for (const folder of childFolders) {
      const id = folder['_id'].toString();
      const cardSets = await this.findAllCardSetsInFolder(null,id);
      const populatedFolder = {
        id: id,
        userId: folder.userId,
        n: cardSets.length,
        name: folder.name,
        color: folder.color,
        parentId: folder.parentId,
      };
      indexedChildFolders.push(populatedFolder);
    }

    // get all card sets with parent id = null
    const childCardSets = await this.findAllCardSetsInFolder(userId, folderId);
    const indexedChildCardSets = [];
    for (const cardSet of childCardSets) {
      const cardSetId = cardSet['_id'].toString();
      const populatedCardSet = await this.cardSetService.getCardSetById(
        cardSetId,
      );
      if (!populatedCardSet) continue;
      indexedChildCardSets.push(populatedCardSet);
    }
    return { folders: indexedChildFolders, cardSets: indexedChildCardSets };
  }

  async createFolder(folder: Folder): Promise<Folder> {
    if(folder.parentId === 'root') folder.parentId = null;
    const createdFolder = new this.FolderModel(folder);
    return await createdFolder.save();
  }

  async updateFolder(id: string, folder: Folder): Promise<Folder> {
    if(folder.parentId === 'root') folder.parentId = null;
    return this.FolderModel.findByIdAndUpdate(id, folder, { new: true }).exec();
  }

  async deleteFolder(id: string, moveContentToRoot = false): Promise<Folder> {
    const folder = await this.FolderModel.findById(id).exec();
    if (!folder) throw new Error('Folder not found');
    const content = await this.getUserFolderContent(folder.userId, id);
    const childFolders = content.folders;
    const childCardSets = content.cardSets;
    for (const childFolder of childFolders) {
      const updatedFolder = childFolder;
      updatedFolder.parentId = null;
      if (moveContentToRoot)
        await this.updateFolder(childFolder['_id'].toString(), updatedFolder);
      else await this.deleteFolder(childFolder['_id'].toString());
    }
    for (const childCardSet of childCardSets) {
      const updatedCardSet = childCardSet;
      updatedCardSet.parentId = null;
      if (moveContentToRoot)
        await this.cardModel
          .findByIdAndUpdate(childCardSet['_id'].toString(), updatedCardSet, {
            new: true,
          })
          .exec();
      else
        await this.cardModel
          .findByIdAndDelete(childCardSet['_id'].toString())
          .exec();
    }
    return this.FolderModel.findByIdAndDelete(id).exec();
  }

  async findAllCardSetsInFolder(
    userId?: string,
    folderId?: string,
  ): Promise<CardsSet[]> {
    if (!folderId && !userId)
      throw new Error('No folder id or user id provided');
    if (!folderId) folderId = null;
    if (userId)
      return this.cardModel.find({ userId: userId, parentId: folderId }).exec();
    else return this.cardModel.find({ parentId: folderId }).exec();
  }
}
